import React, {Component, useState} from 'react';
import UserService from "../service/UserService";
import { DatePicker, Space } from 'antd';
import {
    Form,
    Input,
    Select,
    Checkbox,
    Button
} from 'antd';

function onChange(date, dateString) {
    console.log(date, dateString);
}

const { Option } = Select;


class RegistrationForm extends Component{

    constructor(props) {
        super(props);

        this.state = {
            id: this.props.match.params.id,
            userName: '',
            firstName: '',
            lastName: '',
            email: '',
            phone: '',
            password: '',
            birthday: '',
            gender: ''
        }
        this.changeUserNameHandler = this.changeUserNameHandler.bind(this);
        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
        this.changeEmailHandler = this.changeEmailHandler.bind(this);
        this.changePasswordHandler = this.changePasswordHandler.bind(this);
        this.changePhoneHandler = this.changePhoneHandler.bind(this);
        this.changeBirthdayHandler = this.changeBirthdayHandler.bind(this);
        this.changeGenderHandler = this.changeGenderHandler.bind(this);

        this.saveUser = this.saveUser.bind(this);

    }

    saveUser = (event) => {
        event.preventDefault();

        let user = {
            userName: this.state.userName,
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            email: this.state.email,
            password: this.state.password,
            phone: this.state.phone,
            birthday: this.state.birthday,
            gender: this.state.gender
        };
        console.log('user => ' + JSON.stringify(user));

        UserService.createUser(user).then(res =>{
            this.props.history.push('/users');
        });
    }

    cancel(){
        this.props.history.push('/users');
    }

    //Assign the new values to user
    changeFirstNameHandler= (event) => {
        this.setState({firstName: event.target.value});
    }

    changeLastNameHandler= (event) => {
        this.setState({lastName: event.target.value});
    }

    changeBirthdayHandler= (event) => {
        this.setState({birthday: event.target.value});
    }

    changeGenderHandler= (event) => {
        this.setState({gender: event.target.value});
    }

    changeEmailHandler= (event) => {
        this.setState({email: event.target.value});
    }

    changePasswordHandler= (event) => {
        this.setState({password: event.target.value});
    }


    render()
    {
        return (
            <>
                <Form
                    labelCol={{
                        span: 4,
                    }}
                    wrapperCol={{
                        span: 14,
                    }}
                >

                    <Form.Item
                        name="email"
                        label="E-mail"
                        rules={[
                            {
                                type: 'email',
                                message: 'Email-ul este invalid!',
                            },
                            {
                                required: true,
                                message: 'Introduceți Email-ul!',
                            },
                        ]}
                    >
                        <Input/>
                    </Form.Item>

                    <Form.Item name="age"
                               label="Data Nasterii"
                               rules={[
                                   {
                                       required: true,
                                       message: 'Introduceți data nașterii!',
                                   },
                               ]}>
                        <Space direction="vertical">
                            <DatePicker onChange={onChange}/>
                        </Space>
                    </Form.Item>

                    <Form.Item
                        name="password"
                        label="Parola"
                        rules={[
                            {
                                required: true,
                                min: 5,
                                message: 'Introducreți parola!',
                            },
                        ]}
                        hasFeedback
                    >
                        <Input.Password />
                    </Form.Item>

                    <Form.Item
                        name="confirm"
                        label="Confirmă parola"
                        dependencies={['password']}
                        hasFeedback
                        rules={[
                            {
                                required: true,
                                message: 'Confirmați parola!',
                            },
                            ({getFieldValue}) => ({
                                validator(_, value) {
                                    if (!value || getFieldValue('password') === value) {
                                        return Promise.resolve();
                                    }

                                    return Promise.reject(new Error('Parola nu coincide!'));
                                },
                            }),
                        ]}
                    >
                        <Input.Password/>
                    </Form.Item>

                    <Form.Item
                        name="nickname"
                        label="Nickname"
                        rules={[
                            {
                                required: true,
                                message: 'Introduceți nickname-ul!',
                                whitespace: true,
                            },
                        ]}
                    >
                        <Input/>
                    </Form.Item>

                    <Form.Item
                        name="residence"
                        label="Orasul"
                        rules={[
                            {
                                required: true,
                                message: 'Introduceți orașul',
                            },
                        ]}
                    >
                        <Input/>
                    </Form.Item>

                    <Form.Item
                        name="phone"
                        label="Nr de telefon"
                        rules={[
                            {
                                required: true,
                                min: 8,
                                max: 8,
                                message: 'Introduceți numarul de telefon!',
                            },
                        ]}
                    >

                        <Input type={'number'}
                               prefix="+373"
                        />
                    </Form.Item>

                    <Form.Item
                        name="gender"
                        label="Genul"
                        rules={[
                            {
                                required: true,
                                message: 'Selectați genul!',
                            },
                        ]}
                    >
                        <Select placeholder="selecteaza genul">
                            <Option value="masculin">Masculin</Option>
                            <Option value="feminin">Feminin</Option>
                            <Option value="altele">Altele</Option>
                        </Select>
                    </Form.Item>

                    <Form.Item
                        name="agreement"
                        valuePropName="checked"
                        rules={[
                            {
                                validator: (_, value) =>
                                    value ? Promise.resolve() : Promise.reject(new Error('Should accept agreement')),
                            },
                        ]}

                    >
                        <Checkbox>
                            Sunt de acord cu <a href="">termenii și condițiile</a>
                        </Checkbox>
                    </Form.Item>
                    <Form.Item>
                        <Button type="primary" htmlType="submit">
                            Register
                        </Button>
                    </Form.Item>
                </Form>
            </>
        );
    }
}
export default RegistrationForm;
